INSERT INTO `modx_site_htmlsnippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `static`, `static_file`) VALUES
(1, 0, 0, 'sample.ArticlesLatestPostTpl', 'The tpl row for the latest post. Duplicate this to override it.', 0, 4, 0, '<li>\n  <a href=\"[[~[[+id]]]]\">[[+pagetitle]]</a>\n  [[+publishedon:notempty=`<br /> - [[+publishedon:strtotime:date=`%b %d, %Y`]]`]]\n</li>', 0, NULL, 0, ''),
(2, 0, 0, 'sample.ArticleRowTpl', 'The tpl row for each post when listed on the main Articles Container page. Duplicate this to override it.', 0, 4, 0, '<div class=\"post\">\n    <h2 class=\"title\"><a href=\"[[~[[+id]]]]\">[[+pagetitle]]</a></h2>\n    <p class=\"post-info\">[[%articles.posted_by]] <a href=\"[[~[[*id]]]]author/[[+createdby:userinfo=`username`]]\">[[+createdby:userinfo=`username`]]</a> [[+tv.articlestags:notempty=` | <span class=\"tags\">[[%articles.tags]]: [[!tolinks? &items=`[[+tv.articlestags]]` &target=`[[*id]]` &useTagsFurl=`1`]]</span>`]]</p>\n    <div class=\"entry\">\n	    <p>[[+introtext:default=`[[+content:ellipsis=`400`]]`]]</p>\n    </div>\n    <p class=\"postmeta\">\n      <span class=\"links\">\n        <a href=\"[[~[[+id]]]]\" class=\"readmore\">[[%articles.read_more]]</a>\n        [[+comments_enabled:is=`1`:then=`| <a href=\"[[~[[+id]]]]#comments\" class=\"comments\">[[%articles.comments]] ([[!QuipCount? &thread=`article-b[[+parent]]-[[+id]]`]])</a>`]]\n        | <span class=\"date\">[[+publishedon:strtotime:date=`%b %d, %Y`]]</span>\n      </span>\n    </p>\n</div>', 0, NULL, 0, ''),
(3, 0, 0, 'sample.ArticlesRss', 'The tpl for the RSS feed. Duplicate this to override it.', 0, 4, 0, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rss version=\"2.0\" xml:lang=\"en-US\">\n<channel>\n  <title>[[++site_name]]</title>\n  <link>[[~[[*id]]?scheme=`full`]]</link>\n  <description>[[*description:cdata]]</description>\n  <language>en</language>\n  <copyright>Copyright [[+year]] by [[++site_admin]]. All Rights Reserved.</copyright>\n  <ttl>120</ttl>\n  [[+content]]\n</channel>\n</rss>', 0, NULL, 0, ''),
(4, 0, 0, 'sample.ArticlesRssItem', 'The tpl row for each RSS feed item. Duplicate this to override it.', 0, 4, 0, '<item>\n    <title>[[+pagetitle]]</title>\n    <link>[[~[[+id]]?scheme=`full`]]</link>\n    <description>[[+introtext:default=`[[+content:ellipsis=`400`]]`:cdata]]</description>\n    <pubDate>[[+publishedon:strtotime:date=`%a, %d %b %Y %H:%M:%S %z`]]</pubDate>\n    <guid>[[~[[+id]]?scheme=`full`]]</guid>\n    <author>[[+createdby:userinfo=`email`]] ([[+createdby:userinfo=`fullname`]])</author>\n    [[!ArticlesStringSplitter? &string=`[[+tv.articlestags]]` &tpl=`sample.ArticlesRssCategoryNode`]]\n</item>\n', 0, NULL, 0, ''),
(5, 0, 0, 'sample.ArchiveGroupByYear', 'The tpl wrapper for archives when grouped by year.', 0, 4, 0, '<ul>\n    <li><a href=\"[[+url]]\">[[+year]]</a>\n    <ul>\n        [[+row]]\n    </ul>\n    </li>\n</ul>', 0, NULL, 0, ''),
(6, 0, 0, 'sample.ArticlesRssCategoryNode', 'The tpl for each RSS category node for tagging.', 0, 4, 0, '<category>[[+item]]</category>', 0, NULL, 0, ''),
(7, 0, 0, 'fiDefaultEmailTpl', 'The default chunk used for the email. Please do not edit this chunk, as this will be overwritten when updating FormIt.', 0, 5, 0, '<p>[[+fields]]</p>', 0, 'a:0:{}', 0, ''),
(8, 0, 0, 'fiDefaultFiarTpl', 'The default chunk used for the autoresponder email. Please do not edit this chunk, as this will be overwritten when updating FormIt.', 0, 5, 0, '<p>Hello [[+name]],</p>\n\n<p>Your message has been received. We will respond as soon as possible. Thank you for contacting us.</p>\n\n<p>NOTE: This is an automatic response; please do not respond to this message directly.</p>\n\n<p>Here is your message:<br />\n[[+message:nl2br]]</p>', 0, 'a:0:{}', 0, ''),
(9, 0, 0, 'fiDefaultOptGroupTpl', 'The default chunk used by the FormItCountryOptions snippet for the select optgroup. Please do not edit this chunk, as this will be overwritten when updating FormIt.', 0, 5, 0, '<optgroup label=\"[[+text]]\">\n    [[+options]]\n</optgroup>', 0, 'a:0:{}', 0, ''),
(10, 0, 0, 'fiDefaultOptionTpl', 'The default chunk used by the FormItCountryOptions snippet for the select option. Please do not edit this chunk, as this will be overwritten when updating FormIt.', 0, 5, 0, '<option value=\"[[+value]]\"[[+selected]]>[[+text]]</option>', 0, 'a:0:{}', 0, ''),
(11, 1, 0, 'bootstrap_chunk', '', 0, 10, 0, ' <meta charset=\"UTF-8\">\n        <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n        <!--===============================================================================================-->\n        <link rel=\"stylesheet\" type=\"text/css\" href=\"assets/css/bootstrap.min.css\">\n        <!--===============================================================================================-->\n        <link rel=\"stylesheet\" type=\"text/css\" href=\"assets/fonts/font-awesome-4.7.0/css/font-awesome.min.css\">\n        <!--===============================================================================================-->\n        <link rel=\"stylesheet\" type=\"text/css\" href=\"assets/fonts/iconic/css/material-design-iconic-font.min.css\">\n        <link rel=\"stylesheet\" type=\"text/css\" href=\"assets/css/util.css\">\n        <link rel=\"stylesheet\" type=\"text/css\" href=\"assets/css/main.css\">\n        <link rel=\"stylesheet\" type=\"text/css\" href=\"assets/css/style.css\">\n        <link rel=\"stylesheet\" type=\"text/css\" href=\"assets/css/bootstrap.min.css\">\n        <!--===============================================================================================-->\n        </head>', 0, 'a:0:{}', 0, ''),
(12, 1, 0, 'bootstrap_js_chunk ', '', 0, 10, 0, '\n        <script src=\"assets/vendor/jquery/jquery-3.2.1.min.js \"></script>\n        <!--===============================================================================================-->\n        <script src=\"assets/vendor/animsition/js/animsition.min.js \"></script>\n        <!--===============================================================================================-->\n        <script src=\"assets/vendor/bootstrap/js/popper.js \"></script>\n        <script src=\"assets/vendor/bootstrap/js/bootstrap.min.js \"></script>\n        <!--===============================================================================================-->\n        <script src=\"assets/vendor/select2/select2.min.js \"></script>\n        <!--===============================================================================================-->\n        <script src=\"assets/vendor/daterangepicker/moment.min.js \"></script>\n        <script src=\"assets/vendor/daterangepicker/daterangepicker.js \"></script>\n        <!--===============================================================================================-->\n        <script src=\"assets/vendor/countdowntime/countdowntime.js \"></script>\n        <!--===============================================================================================-->\n        <script src=\"assets/js/main.js \"></script>\n', 0, 'a:0:{}', 0, ''),
(13, 1, 0, 'formulaire', '', 0, 8, 0, '[[!FormIt?\n   &hooks=`email`\n   &emailTpl=`emailChunk`\n   &emailTo=`user@example.com`\n]]\n\n<form action=\"[[~[[+id]]]]\" method=\"post\">\n \n    <label for=\"nom\">Nom</label>\n \n    <input type=\"text\" name=\"nom\" id=\"nom\" value=\"[[!+fi.nom]]\">\n \n    <label for=\"email\">Email</label>\n     \n    <input type=\"text\" name=\"email\" id=\"email\" value=\"[[!+fi.email]]\">\n \n    <label for=\"description\">description</label>\n \n    <textarea name=\"description\" id=\"description\" cols=\"30\" rows=\"10\" value=\"[[!+fi.description]]\"></textarea>\n \n    <input type=\"submit\" value=\"Submit\">\n \n</form>', 0, 'a:0:{}', 0, ''),
(14, 1, 0, 'emailChunk', '', 0, 8, 0, '<p>Hello,</p>\n<p>[[+nomresponsable]].</p>\n<p>Their email: [[+emailresponsable]]</p>\n<p>Thanks!</p>', 0, 'a:0:{}', 0, ''),
(15, 1, 0, 'formulaire_institution', '', 0, 8, 0, '[[$bootstrap_chunk]]\n<body>\n[[!FormIt?\n       &hooks=`insertion2,FormItSaveForm`\n       &redirectTo=`2`\n       &emailTpl=`emailChunk`\n       &emailTo=`user@example.com`\n       &emailFrom=`sasa@gmail.com`\n       &emailFromName=`sasa`\n       &forName=`azert`\n    ]]\n <div class=\"container-contact\">\n\n      <div class=\"wrap-contact\">\n            <form class=\"contact-form validate-form\" action=\"[[~[[*id]]]]\" method=\"post\">\n              \n        \n                    <span class=\"contact-form-title\">\n                        Informations visibles du public\n                    </span>\n                    <div class=\"row\">\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir le nom de l\'association\">\n                                <input value=\"[[!+fi.nomassociation]]\" class=\"input\" type=\"text\" name=\"nomassociation\" placeholder=\"Nom de l\'association\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir le sigle\">\n                                <input value=\"[[!+fi.sigle]]\" class=\"input\" type=\"text\" name=\"sigle\" placeholder=\"Sigle\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                    </div>\n                    <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir la description\">\n                        <textarea value=\"[[!+fi.description]]\" class=\"input\" name=\"description\" placeholder=\"Description de l\'association\"></textarea>\n                        <span class=\"focus-input\"></span>\n                    </div>\n                    <div class=\"row\">\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir l\'adresse de l\'association\">\n                                <input value=\"[[!+fi.adresse]]\" class=\"input\" type=\"text\" name=\"adresse\" placeholder=\"adresse\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                        \n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir le numero de téléphone\">\n                                <input value=\"[[!+fi.telephone]]\" class=\"input\" type=\"tel\" name=\"telephone\" placeholder=\"Téléphone\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                    </div>\n                    <div class=\"row\">\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisr vos heures d\'ouverture\">\n                                <input value=\"[[!+fi.heure]]\" class=\"input\" type=\"text\" name=\"heure\" placeholder=\"Horaires d\'ouverture\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\">\n                                <input value=\"[[!+fi.site]]\" class=\"input\" type=\"text\" name=\"site\" placeholder=\"Site internet\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                    </div>\n                    \n                    <span class=\"contact-form-title\">\n                        Informations responsable des bénévoles\n                    </span>\n                    <div class=\"row\">\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir le nom du responsable\">\n                                <input value=\"[[!+fi.nomresponsable]]\" class=\"input\" type=\"text\" name=\"nomresponsable\" placeholder=\"Nom complet\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir un email valide\">\n                                <input value=\"[[!+fi.emailresponsable]]\" class=\"input\" type=\"email\" name=\"emailresponsable\" placeholder=\"Email\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                    </div>\n                    <span class=\"contact-form-title\">\n                        Informations complmentaires\n                    </span>\n\n                    <div class=\"row\">\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir l\'effectif de vos salariés\">\n                                <input value=\"[[!+fi.effectifsalarie]]\" class=\"input\" type=\"number\" name=\"effectifsalarie\" placeholder=\"Effectif salarié\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir l\'effectif de vos bénévoles\">\n                                <input value=\"[[!+fi.effectifbenevole]]\" class=\"input\" type=\"number\" name=\"effectifbenevole\" placeholder=\"Effectif bénévole\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                    </div>\n                    <div class=\"row\">\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir le nom du president\">\n                                <input value=\"[[!+fi.nompresident]]\" class=\"input\" type=\"text\" name=\"nompresident\" placeholder=\"Nom complet du president de l\'association\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir un email valide\">\n                                <input value=\"[[!+fi.emailpresident]]\" class=\"input\" type=\"email\" name=\"emailpresident\" placeholder=\"Email du president\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                    </div>\n                    <div class=\"container-contact-form-btn\">\n                        <button type=\"submit\" class=\"contact-form-btn\">\n                            Enregistrer\n                        </button>\n                    </div>\n                \n            </form>\n    </div>\n    </div>\n ', 0, 'a:0:{}', 0, ''),
(16, 1, 0, 'insc_benevole', '', 0, 8, 0, '[[$bootstrap_chunk]]\n\n    <body>\n\n        [[!FormIt?\n        &hooks=`insertion,FormItSaveForm`\n        &redirectTo=`3` \n        &emailTpl=`MymailChunk` \n        &emailTo=`sasa@gmail.com` \n        &emailFrom=`fifi@gmail.com` \n        &emailFromName=`fifi`\n        &formName=`Quote Enquiry`\n        ]]\n\n\n        <div class=\"container-contact\">\n            <div class=\"wrap-contact\">\n                <form class=\"contact-form validate-form\" action=\" \" method=\"POST\">\n                    <span class=\"contact-form-title\">\n					Inscrivez vous\n				</span>\n                    <div class=\"row\">\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir votre nom \">\n                                <input class=\"input\" type=\"text\" name=\"name\" value=\"[[+!.fi.name]]\" placeholder=\"Votre Nom\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir votre prénomt\">\n                                <input class=\"input\" type=\"text\" name=\"prenom\" value=\"[[+!.fi.prenom]]\" placeholder=\"Votre Prénom\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                    </div>\n                    <div class=\"row\">\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir un Email valide\">\n                                <input class=\"input\" type=\"email\" name=\"mail\" value=\"[[+!.fi.mail]]\" placeholder=\"Votre Email\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir votre Téléphone \">\n                                <input class=\"input\" type=\"text\" name=\"tel\" value=\"[[+!.fi.tel]]\" placeholder=\"Votre Téléphone\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                    </div>\n                    <div class=\"row\">\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir votre Année de naissance\">\n                                <input class=\"input\" type=\"number\" name=\"naissance\" value=\"[[+!.fi.naissance]]\" placeholder=\"Année de Naissance\">\n                                <span class=\"focus-input\"></span>\n                            </div>\n                        </div>\n                        <div class=\"col\">\n                            <div class=\"wrap-input validate-input\" data-validate=\"Veuillez saisir un adresse valide\">\n                                <input class=\"input\" type=\"text\" name=\"adresse\" value=\"[[+!.fi.adresse]]\" placeholder=\"Adresse\">\n\n                            </div>\n                        </div>\n                    </div>\n                    <h4>Civilité</h4>\n                    <div class=\"form-check form-check-inline\">\n                        <label class=\"form-check-label\" for=\"inlineRadio1\"> <input class=\"form-check-input\" type=\"radio\" name=\"civil\" value=\"femme\" [[+!.fi.civil : FormItIsChecked=`femme`]]> Femme</label>\n\n                    </div>\n                    <div class=\"form-check form-check-inline\">\n                        <label class=\"form-check-label\" for=\"inlineRadio2\"><input class=\"form-check-input\" type=\"radio\" name=\"civ