(39, 1, 0, 'registration_benev', '', 0, 0, 0, '/**\n    * benevoles table\n    */\n    $output = \'\';\n   \n    $package_path = $modx->getOption(\'core_path\').\'components/appiut/model/\';\n    $modx->addPackage(\'appiut\', $package_path);\n    $myRow = $modx->newObject(\'Benevoles\');\n    echo  $modx->runSnippet(\'suppression_benevoles\',array(\n        \'id\'=> $_POST[\'benevoles_id\']\n        \n        ));\n    $output .= \'\n    <div class=\"table-responsive\">\n        <h2>Liste des bénévoles</h2>\n        <table class=\"table\">\n            <thead class=\"thead-light\">\n                <tr>\n                    <th>ID</th>\n                    <th>Nom</th>\n                    <th>Prenom</th>\n                    <th>Adresse</th>\n                    <th>Telephone</th>\n                    <th>email</th>\n                    <th>Civilite</th>\n                    <th>Datenaissance</th>\n                    <th>Actif</th>\n                    <th>Competence</th>\n                    <th>Formation Experience</th>\n                    <th>Suppression</th>\n                <tr>\n            </thead>\';\n            $query = $modx->newQuery(\'Benevoles\');\n            $rows = $modx->getIterator(\'Benevoles\', $query);\n            $list = array();\n            foreach ($rows as $row) {\n            $row_array = $row->toArray();\n            \n            $output .= \'\n            <tbody class=\"points_table_scrollbar\">\n                <tr class=\"odd\">\n                    <td>\'.$row_array[\'id\'].\'</td>\n                    <td>\'.$row_array[\'nom\'].\'</td>\n                    <td>\'.$row_array[\'prenom\'].\'</td>\n                    <td>\'.$row_array[\'adresse\'].\'</td>\n                    <td>\'.$row_array[\'telephone\'].\'</td>\n                    <td>\'.$row_array[\'email\'].\'</td>\n                    <td>\'.$row_array[\'civilite\'].\'</td>\n                    <td>\'.$row_array[\'anneenaissance\'].\'</td>\n                    <td>\'.$row_array[\'actif\'].\'</td>\n                    <td>\'.$row_array[\'competence\'].\'</td>\n                    <td>\'.$row_array[\'experience\'].\'</td>\n                    <td> \n                        <form method=\"post\">\n                            <input type=\"hidden\" name=\"benevoles_id\" value=\"\'.$row_array[\'id\'].\'\">\n                            <button onclick=\"return confirm(\\\'Etes vous sur de voiloir supprimer?\\\');\" class=\"btn btn-danger\" name=\"supprimer\">Supprimer</button>\n                        </form>\n                    </td>\n                </tr>\n            </tbody>\';\n            }\n        $output .= \'\n        </table>\n        \n    </div>\';\n    return $output;', 0, 'a:0:{}', '', 0, ''),
(40, 1, 0, 'suppression_benevoles', '', 0, 9, 0, '$param=$modx->event->params;\n$id= (int) $param[\'id\'];\nif (isset($id) && !empty($id) && is_int($id) && $id>0){\n        $package_path = $modx->getOption(\'core_path\').\'components/appiut/model/\';\n        $modx->addPackage(\'appiut\', $package_path);\n        $box = $modx->getObject(\'Benevoles\',$id);\n        if($box->remove() == false) {\n           return \"An error occurred while trying to remove the box!\";\n        } else return \"Suppression success\";\n}', 0, 'a:0:{}', '', 0, ''),
(41, 1, 0, 'suppression_institutions', '', 0, 9, 0, '$param=$modx->event->params;\n$id= (int) $param[\'id\'];\nif (isset($id) && !empty($id) && is_int($id) && $id>0){\n        $package_path = $modx->getOption(\'core_path\').\'components/appiut/model/\';\n        $modx->addPackage(\'appiut\', $package_path);\n        $box = $modx->getObject(\'Insitutions\',$id);\n        if($box->remove() == false) {\n           return \"An error occurred while trying to remove the box!\";\n        } else return \"Suppression success\";\n}', 0, 'a:0:{}', '', 0, '');
