(2, 0, 0, 'sample.ArticlesContainerTemplate', 'The default Template for the Articles Container. Duplicate this to override it.', 0, 4, '', 0, '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" lang=\"en\">\n<head>\n<title>Articles - [[*pagetitle]]</title>\n<meta charset=\"utf-8\">\n<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge,chrome=1\">\n<link rel=\"stylesheet\" type=\"text/css\" href=\"[[++articles.assets_url:default=`[[++base_url]]assets/components/articles/`]]themes/default/style.css\" />\n<base href=\"[[++site_url]]\" />\n</head>\n<body>\n\n<div id=\"header-wrap\">\n  <div id=\"header\" class=\"container_16\">\n    <h1 id=\"logo-text\"><a href=\"[[~[[*id]]]]\" title=\"\">Articles</a></h1>\n    <p id=\"intro\">Articles By Me</p>\n    <!-- navigation -->\n    <div id=\"nav\">\n      <ul><li class=\"first\"><a href=\"[[~[[*id]]]]\" title=\"Home\" >Home</a></li></ul>\n    </div>\n    <div id=\"header-image\"></div>\n      <div id=\"search\">\n\n<form id=\"quick-search\" action=\"search-results.html\" method=\"get\">\n<p>\n  <label for=\"qsearch\">Search:</label>\n  <input class=\"tbox\" id=\"qsearch\" type=\"text\" name=\"search\" value=\"\" title=\"Start typing and hit ENTER\" />\n  <input class=\"btn\" alt=\"Search\" type=\"image\" title=\"Search\" src=\"[[++articles.assets_url:default=`assets/components/articles/`]]themes/default/images/search.gif\" />\n</p>\n</form>\n</div>\n      \n      <!-- header ends here -->\n  </div>\n</div>\n<!-- content starts -->\n<div id=\"content-outer\"><div id=\"content-wrapper\" class=\"container_16\">\n\n<!-- main -->\n<div id=\"main\" class=\"grid_12\">\n[[*content]]\n</div>\n\n<!-- main ends -->\n\n\n<div id=\"left-columns\" class=\"grid_4\">\n  <div class=\"grid_4 alpha\">\n\n<div class=\"sidemenu\">\n  <h3>Latest Posts</h3>\n  <ul>\n  [[+latest_posts]]\n  </ul>\n</div>\n\n[[+comments_enabled:is=`1`:then=`\n<div class=\"sidemenu\">\n  <h3>Latest Comments</h3>\n  <ul>\n  [[+latest_comments]]\n  </ul>\n</div>\n`]]\n\n\n  </div>\n  <!-- end left-columns -->\n</div>\n<!-- contents end here -->\n\n</div></div>\n\n<!-- footer starts here -->\n<div id=\"footer-wrapper\" class=\"container_12\">\n\n  <div id=\"footer-content\">\n    <div class=\"grid_4\">\n<h3>Tags</h3>\n[[+tags]]\n    </div>\n    <div class=\"grid_4\">\n  <h3>Archives</h3>\n  [[+archives]]\n    </div>\n  </div>\n  <div id=\"footer-bottom\">\n   <p class=\"bottom-left\">\n&nbsp; &copy; 2010-2012 Articles. all rights reserved.\n      &nbsp; &nbsp; powered by <a href=\"http://modx.com/\">modx revolution</a>\n      &nbsp; &nbsp; <a href=\"http://www.bluewebtemplates.com/\" title=\"Website Templates\">website templates</a> by <a href=\"http://www.styleshout.com/\">styleshout</a>\n      </p>\n\n      <p class=\"bottom-right\" >\n        <a href=\"[[~1]]\">Home</a> |\n        <a href=\"[[~1]]\">Sitemap</a> |\n        <a href=\"http://jigsaw.w3.org/css-validator/check/referer\">CSS</a> |\n             <a href=\"http://validator.w3.org/check/referer\">XHTML</a>\n      </p>\n\n  </div>\n</div>\n\n</body>\n</html>', 0, NULL, 0, ''),
(3, 0, 0, 'sample.ArticleTemplate', 'The default Template for an Article. Duplicate this to override it.', 0, 4, '', 0, '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" lang=\"en\">\n<head>\n<title>Articles - [[*pagetitle]]</title>\n<meta charset=\"utf-8\">\n<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge,chrome=1\">\n<link rel=\"stylesheet\" type=\"text/css\" href=\"[[++articles.assets_url:default=`[[++base_url]]assets/components/articles/`]]themes/default/style.css\" />\n<base href=\"[[++site_url]]\" />\n</head>\n<body>\n\n<div id=\"header-wrap\">\n  <div id=\"header\" class=\"container_16\">\n    <h1 id=\"logo-text\"><a href=\"[[~[[*parent]]]]\" title=\"\">Articles</a></h1>\n    <p id=\"intro\">Articles By Me</p>\n    <!-- navigation -->\n    <div id=\"nav\">\n      <ul><li class=\"first\"><a href=\"[[~[[*id]]]]\" title=\"Home\" >Home</a></li>\n    </div>\n    <div id=\"header-image\"></div>\n      <div id=\"search\">\n\n<form id=\"quick-search\" action=\"search-results.html\" method=\"get\">\n<p>\n  <label for=\"qsearch\">Search:</label>\n  <input class=\"tbox\" id=\"qsearch\" type=\"text\" name=\"search\" value=\"\" title=\"Start typing and hit ENTER\" />\n  <input class=\"btn\" alt=\"Search\" type=\"image\" title=\"Search\" src=\"[[++articles.assets_url:default=`assets/components/articles/`]]themes/default/images/search.gif\" />\n</p>\n</form>\n</div>\n      <!-- header ends here -->\n  </div>\n</div>\n<!-- content starts -->\n<div id=\"content-outer\"><div id=\"content-wrapper\" class=\"container_16\">\n\n<!-- main -->\n<div id=\"main\" class=\"grid_12\">\n    <h2 class=\"title\"><a href=\"[[~[[*id]]]]\">[[*pagetitle]]</a></h2>\n    <p class=\"post-info\">\n        <span class=\"left\">Posted on [[*publishedon:strtotime:date=`%b %d, %Y`]] by <a href=\"[[~[[*parent]]]]author/[[*publishedby:userinfo=`username`]]\">[[*publishedby:userinfo=`username`]]</a></span>\n[[*articlestags:notempty=`\n        <span class=\"tags left\">&nbsp;| Tags: [[+article_tags]]</span>\n`]]\n        [[+comments_enabled:is=`1`:then=`&nbsp;| <a href=\"[[~[[*id]]]]#comments\" class=\"comments\">Comments ([[+comments_count]])</a>`]]\n    </p>\n    <div class=\"entry\">\n        <p>[[*introtext]]</p>\n        <hr />\n        [[*content]]\n    </div>\n\n    <hr />\n\n    <div class=\"post-comments\" id=\"comments\">\n        [[+comments]]\n        <br />\n        <h3>Add a Comment</h3>\n        [[+comments_form]]\n    </div>\n</div>\n\n<div id=\"left-columns\" class=\"grid_4\">\n  <div class=\"grid_4 alpha\">\n\n    <div class=\"sidemenu\">\n      <h3>Latest Posts</h3>\n      <ul>\n      [[+latest_posts]]\n      </ul>\n    </div>\n\n    [[+comments_enabled:is=`1`:then=`\n    <div class=\"sidemenu\">\n      <h3>Latest Comments</h3>\n      <ul>\n      [[+latest_comments]]\n      </ul>\n    </div>\n    `]]\n  </div>\n  <!-- end left-columns -->\n</div>\n<!-- contents end here -->\n\n\n</div></div>\n\n<!-- footer starts here -->\n<div id=\"footer-wrapper\" class=\"container_12\">\n\n  <div id=\"footer-content\">\n    <div class=\"grid_4\">\n<h3>Tags</h3>\n[[+tags]]\n    </div>\n    <div class=\"grid_4\">\n  <h3>Archives</h3>\n  [[+archives]]\n    </div>\n  </div>\n  <div id=\"footer-bottom\">\n   <p class=\"bottom-left\">\n&nbsp; &copy; 2010-2012 Articles. all rights reserved.\n      &nbsp; &nbsp; powered by <a href=\"http://modx.com/\">modx revolution</a>\n      &nbsp; &nbsp; <a href=\"http://www.bluewebtemplates.com/\" title=\"Website Templates\">website templates</a> by <a href=\"http://www.styleshout.com/\">styleshout</a>\n      </p>\n\n      <p class=\"bottom-right\" >\n        <a href=\"[[~1]]\">Home</a> |\n        <a href=\"[[~1]]\">Sitemap</a> |\n        <a href=\"http://jigsaw.w3.org/css-validator/check/referer\">CSS</a> |\n             <a href=\"http://validator.w3.org/check/referer\">XHTML</a>\n      </p>\n\n  </div>\n</div>\n\n</body>\n</html>', 0, NULL, 0, ''),
